<span class="line">  document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">"email"</span><span class="token punctuation">)</span><span class="token punctuation">.</span>value <span class="token operator">=</span> stepOneData<span class="token punctuation">.</span>email<span class="token punctuation">;</span></span>
